// hooks/use-form.ts
'use client'

import { useState, useCallback, ChangeEvent, FormEvent } from 'react'

interface ValidationRule<T = any> {
  validator: (value: T, formData?: Record<string, any>) => boolean | string
  message: string
}

interface UseFormOptions<T> {
  initialValues: T
  onSubmit: (values: T) => Promise<void> | void
  validate?: (values: T) => Partial<Record<keyof T, string>>
  validationRules?: Partial<Record<keyof T, ValidationRule[]>>
}

export function useForm<T extends Record<string, any>>({
  initialValues,
  onSubmit,
  validate,
  validationRules,
}: UseFormOptions<T>) {
  const [values, setValues] = useState<T>(initialValues)
  const [errors, setErrors] = useState<Partial<Record<keyof T, string>>>({})
  const [isSubmitting, setIsSubmitting] = useState(false)
  const [touched, setTouched] = useState<Partial<Record<keyof T, boolean>>>({})

  const validateField = useCallback((name: keyof T, value: any) => {
    let error = ''

    // Custom validation function
    if (validate) {
      const validationErrors = validate({ ...values, [name]: value } as T)
      if (validationErrors[name]) {
        error = validationErrors[name]!
      }
    }

    // Validation rules
    if (!error && validationRules?.[name]) {
      for (const rule of validationRules[name]!) {
        const isValid = rule.validator(value, values)
        if (isValid !== true) {
          error = typeof isValid === 'string' ? isValid : rule.message
          break
        }
      }
    }

    return error
  }, [values, validate, validationRules])

  const handleChange = useCallback((
    e: ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>
  ) => {
    const { name, value, type } = e.target
    const fieldName = name as keyof T

    let finalValue: any = value

    if (type === 'checkbox') {
      finalValue = (e.target as HTMLInputElement).checked
    } else if (type === 'number') {
      finalValue = value === '' ? '' : Number(value)
    }

    setValues(prev => ({ ...prev, [fieldName]: finalValue }))

    // Validate on change if touched
    if (touched[fieldName]) {
      const error = validateField(fieldName, finalValue)
      setErrors(prev => ({
        ...prev,
        [fieldName]: error,
      }))
    }
  }, [touched, validateField])

  const handleBlur = useCallback((
    e: ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>
  ) => {
    const name = e.target.name as keyof T
    setTouched(prev => ({ ...prev, [name]: true }))

    const error = validateField(name, values[name])
    setErrors(prev => ({
      ...prev,
      [name]: error,
    }))
  }, [values, validateField])

  const handleSubmit = useCallback(async (e: FormEvent) => {
    e.preventDefault()

    // Validate all fields
    const newErrors: Partial<Record<keyof T, string>> = {}
    let isValid = true

    Object.keys(values).forEach((key) => {
      const fieldName = key as keyof T
      const error = validateField(fieldName, values[fieldName])
      if (error) {
        newErrors[fieldName] = error
        isValid = false
      }
    })

    setErrors(newErrors)
    setTouched(Object.keys(values).reduce((acc, key) => ({
      ...acc,
      [key]: true,
    }), {}))

    if (!isValid) {
      return
    }

    try {
      setIsSubmitting(true)
      await onSubmit(values)
    } catch (error) {
      console.error('Form submission error:', error)
    } finally {
      setIsSubmitting(false)
    }
  }, [values, onSubmit, validateField])

  const reset = useCallback(() => {
    setValues(initialValues)
    setErrors({})
    setTouched({})
  }, [initialValues])

  const setFieldValue = useCallback((name: keyof T, value: any) => {
    setValues(prev => ({ ...prev, [name]: value }))

    if (touched[name]) {
      const error = validateField(name, value)
      setErrors(prev => ({
        ...prev,
        [name]: error,
      }))
    }
  }, [touched, validateField])

  return {
    values,
    errors,
    touched,
    isSubmitting,
    handleChange,
    handleBlur,
    handleSubmit,
    reset,
    setFieldValue,
    setValues,
    isValid: Object.keys(errors).length === 0,
  }
}
