'use client'

import { useEffect, useCallback } from 'react'

interface PerformanceMetrics {
  fps: number
  memoryUsage?: number
  loadTime: number
}

export function usePerformanceMetrics(
  onMetricsChange?: (metrics: PerformanceMetrics) => void,
  sampleInterval: number = 1000
) {
  const [metrics, setMetrics] = useState<PerformanceMetrics>({
    fps: 60,
    loadTime: 0,
  })

  const calculateFPS = useCallback(() => {
    let frameCount = 0
    let lastTime = performance.now()

    const checkFPS = () => {
      frameCount++
      const currentTime = performance.now()

      if (currentTime >= lastTime + sampleInterval) {
        const fps = Math.round((frameCount * 1000) / (currentTime - lastTime))

        setMetrics(prev => {
          const newMetrics = {
            ...prev,
            fps,
            memoryUsage: (performance as any).memory?.usedJSHeapSize,
          }

          onMetricsChange?.(newMetrics)
          return newMetrics
        })

        frameCount = 0
        lastTime = currentTime
      }

      requestAnimationFrame(checkFPS)
    }

    return checkFPS
  }, [sampleInterval, onMetricsChange])

  useEffect(() => {
    const checkFPS = calculateFPS()
    const animationId = requestAnimationFrame(checkFPS)

    // Measure initial load time
    if (typeof window !== 'undefined') {
      const loadTime = window.performance.timing.domContentLoadedEventEnd -
                      window.performance.timing.navigationStart

      setMetrics(prev => ({ ...prev, loadTime }))
    }

    return () => {
      cancelAnimationFrame(animationId)
    }
  }, [calculateFPS])

  return metrics
}
